javascript.onUpdate:
if (corDoPolvo == "Laranja") {
        g.setFilling('figAzul1', g.getValue('opacidade') / 100)
        g.setFilling('figAzul2', g.getValue('opacidade') / 100)
        g.setFilling('figAzul3', g.getValue('opacidade') / 100)
}
else {
        g.setFilling('figPolvo1', g.getValue('opacidade') / 100)
        g.setFilling('figPolvo2', g.getValue('opacidade') / 100)
        g.setFilling('figPolvo3', g.getValue('opacidade') / 100)
}

if (g.getValue('opacidade') == 100) {if (corDoPolvo == "Laranja") {corDoPolvo = "Azul"} else {corDoPolvo = "Laranja"}}
